<script setup>import { ref, computed, onMounted, watch } from "vue";import symbols from "../creationFlow/all-symbols.json";import { useLogoStore } from "~/stores/logoStores";import BottomCreationFlowContent from "~/components/CreationFlow/BottomCreationFlowContent.vue";const logoStore = useLogoStore();console.log(symbols);const preferredSymbolsOrStyles = computed({  get: () => logoStore.preferredSymbolsOrStyles,  set: val => { logoStore.preferredSymbolsOrStyles = val }})const symbolsList = ref(symbols);// toggle by matching on symbol.name, max 3 selectionsconst toggleSymbolOrStyle = (symbol) => {  const idx = preferredSymbolsOrStyles.value.findIndex(      s => s.name === symbol.name  )  if (idx > -1) {    // already selected → remove    preferredSymbolsOrStyles.value.splice(idx, 1)  } else if (preferredSymbolsOrStyles.value.length < 3) {    // under limit → add    preferredSymbolsOrStyles.value.push(symbol)  } else {    // at limit → you could alert or just ignore    // alert('You can only select up to 3 symbols.')  }}// match active by nameconst isActive = (symbol) =>    preferredSymbolsOrStyles.value.some(        s => s.name === symbol.name)// Computed property to check if selection is valid (at least one symbol selected)const isValidSelection = computed(() => preferredSymbolsOrStyles.value.length > 0);// Step navigationconst advanceCreationStep = () => {  logoStore.nextStep();};const previousCreationStep = () => {  logoStore.previousStep();};// Initialize values on mountonMounted(() => {});</script><template>  <section class="creation-step step-5 w-full relative flex flex-col justify-start items-start font-primary mt-[69px] md:mt-[80px] lg:mt-[95px]">    <div class="creation-flow-content w-full relative">      <!-- Top Prev/Next Buttons on Desktop -->      <div class="top-desktop-buttons relative flex-row justify-between hidden lg:flex pt-[66px] mx-auto w-[90%] site-container">        <!-- Previous step  -->        <button            @click.prevent="previousCreationStep"            class="previous-step-button flex flex-row gap-[12px] py-[25px] px-[24px] max-w-fit border-[4px] border-black uppercase font-black text-[18px] leading-[2px] tracking-[1px] text-neutral-700 items-center h-[68px] transition transition-fast">          <svg width="14" height="12" viewBox="0 0 14 12" fill="none" xmlns="http://www.w3.org/2000/svg">            <path fill-rule="evenodd" clip-rule="evenodd"                  d="M6.65829 11.4917C6.20268 11.9473 5.46399 11.9473 5.00838 11.4917L0.341709 6.82504C-0.113903 6.36943 -0.113903 5.63074 0.341709 5.17512L5.00838 0.508457C5.46399 0.052845 6.20268 0.052845 6.65829 0.508457C7.1139 0.964069 7.1139 1.70276 6.65829 2.15837L3.98325 4.83342L12.8333 4.83341C13.4777 4.83341 14 5.35575 14 6.00008C14 6.64441 13.4777 7.16675 12.8333 7.16675H3.98325L6.65829 9.84179C7.1139 10.2974 7.1139 11.0361 6.65829 11.4917Z"                  fill="#1F2937"/>          </svg>          Back One Step        </button>        <!-- Next step  -->        <button            @click.prevent="advanceCreationStep"            class="next-step-button flex flex-row gap-[12px] py-[25px] px-[24px] max-w-fit uppercase font-black text-[18px] leading-[1] tracking-[2px] text-white bg-brand-200 items-center h-[68px] transition transition-fast"            :class="isValidSelection ? 'opacity-100 pointer-events-auto' : 'opacity-50 pointer-events-none'"            :disabled="!isValidSelection">          Continue          <svg width="14" height="12" viewBox="0 0 14 12" fill="none" xmlns="http://www.w3.org/2000/svg">            <path fill-rule="evenodd" clip-rule="evenodd"                  d="M7.34171 0.508457C7.79732 0.0528452 8.53601 0.0528451 8.99162 0.508457L13.6583 5.17512C14.1139 5.63074 14.1139 6.36943 13.6583 6.82504L8.99162 11.4917C8.53601 11.9473 7.79732 11.9473 7.34171 11.4917C6.8861 11.0361 6.8861 10.2974 7.34171 9.84179L10.0168 7.16675L1.16667 7.16675C0.522335 7.16675 -2.81646e-08 6.64441 0 6.00008C2.81647e-08 5.35575 0.522335 4.83342 1.16667 4.83342H10.0168L7.34171 2.15837C6.8861 1.70276 6.8861 0.964069 7.34171 0.508457Z"                  fill="white"/>          </svg>        </button>      </div>      <!-- Step 5 Content -->      <div class="step-content site-container pt-[34px] lg:pt-[64px]">        <h1 class="block relative text-black font-bold text-[20px] md:text-[26px] lg:text-[32px] leading-[1.2]">Choose your preferred symbols or styles</h1>        <h2 class="block relative text-black font-normal text-[18px] md:text-[20px] lg:text-[24px] pt-[20px] leading-[1.35]">Choose 2-3 which resonate with your brand</h2>        <!-- Symbol Selection Buttons -->        <div class="symbol-selection-buttons w-full relative flex flex-row flex-wrap justify-start pt-[35px] gap-[24px]">          <button              v-for="symbol in symbolsList"              :key="symbol"              @click="toggleSymbolOrStyle(symbol)"              class="border-2 transition transition-fast"              :class="['button-item', isActive(symbol) ? 'active-button-selection border-brand-200 bg-[#D5F7F3]' : 'border-neutral-400', 'bg-transparent w-full max-w-fit text-neutral-600 font-bold text-[20px] rounded-full flex flex-row items-center justify-center py-[16px] px-[24px] leading-[1]']">              <span class="text">                   {{ symbol.name }}              </span>            <span class="icon max-w-fit transition transition-fast"                  :class="['button-item', isActive(symbol) ? 'opacity-100 w-auto pl-[12px]' : '', 'opacity-0 w-0 p-0']">              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">                  <path fill-rule="evenodd" clip-rule="evenodd" d="M12 24C18.6274 24 24 18.6274 24 12C24 5.37258 18.6274 0 12 0C5.37258 0 0 5.37258 0 12C0 18.6274 5.37258 24 12 24ZM17.5607 10.0607C18.1464 9.47487 18.1464 8.52513 17.5607 7.93934C16.9749 7.35355 1