<script setup>import { ref, computed, watch, onMounted, onUnmounted, inject, provide } from "vue";import isEqual from "lodash/isEqual";import Fuse from "fuse.js";import { useLogoStore } from '~/stores/logoStores';const logoStore = useLogoStore();import { useToast } from 'vue-toastification'const toast = useToast()// json data for all of the list of the following itemsimport industryData from "../all-industries.json";import allSuffixes from "../all-suffixes.json";import allSymbols from "../all-symbols.json";// regular imported components and itemsimport LogoResults from "./LogoResults.vue";import ImageElement from "../../ImageElement.vue";import SaveLogoModal from "~/components/CreationFlow/SaveLogoModal.vue";import CustomizeBuySaveLogo from "~/components/CreationFlow/LogoBuilder/CustomizeBuySaveLogo.vue";const getCurrentLogoFieldValues = () => ({  selectedIndustry: logoStore.selectedIndustry,  selectedLogoStyle: logoStore.selectedLogoStyle,  selectedSuffix: logoStore.selectedSuffix,  sloganOrTagline: logoStore.sloganOrTagline,  selectedColorStyles: [...logoStore.selectedColorStyles],  preferredSymbolsOrStyles: [...logoStore.preferredSymbolsOrStyles],  fullBusinessName: logoStore.fullBusinessName,  businessNameInitials: logoStore.businessNameInitials,  businessCategory: logoStore.businessCategory,});// Local copies for tracking original and last saved states.const originalLogoFieldValues = ref({});const lastSavedLogoValues = ref({});const maxSaveHistory = 15;const updateButtonIsActive = ref(false);// Check whether the current state differs from the last saved state.const checkUpdateButton = () => {  const currentState = getCurrentLogoFieldValues();  const lastState = lastSavedLogoValues.value;  if (!isEqual(currentState, lastState)) {    updateButtonIsActive.value = true;    console.log("Update button enabled (state changed).");  } else {    updateButtonIsActive.value = false;    console.log("State unchanged; update button disabled.");  }};// Save state to history via the store.const saveStateToHistory = () => {  const currentState = getCurrentLogoFieldValues();  const lastState = logoStore.undoHistory[logoStore.undoHistory.length - 1];  if (!isEqual(currentState, lastState)) {    console.log("State changed – pushing new state to history.");    logoStore.pushUndoHistory(currentState, maxSaveHistory);  } else {    console.log("State unchanged – not pushing to history.");  }};console.log(logoStore.redoHistory);// When the update button is clicked, save a new state.const updateProps = () => {  saveStateToHistory();  lastSavedLogoValues.value = getCurrentLogoFieldValues();  updateButtonIsActive.value = false;  console.log("Update pressed – state saved globally.");};// Undo: pop the current state from the store's undoHistory,// push it into redoHistory, then restore the previous state.const undo = () => {  if (logoStore.undoHistory.length >= 2) {    const lastState = JSON.parse(JSON.stringify(logoStore.popUndoHistory()));    logoStore.pushRedoHistory(lastState, maxSaveHistory);    const previousState = JSON.parse(JSON.stringify(logoStore.undoHistory[logoStore.undoHistory.length - 1]));    logoStore.restoreState(previousState);    checkUpdateButton();  }};const redo = () => {  if (logoStore.redoHistory.length > 0) {    const nextState = JSON.parse(JSON.stringify(logoStore.popRedoHistory()));    logoStore.pushUndoHistory(nextState, maxSaveHistory);    logoStore.restoreState(nextState);    checkUpdateButton();  }};// industry selection states & Industry fieldsconst industryInputRef = ref(null);const selectedIndustry = computed({  get: () => logoStore.selectedIndustry,  set: (val) => { logoStore.selectedIndustry = val; }});const industrySearchQuery = computed({  get: () => (logoStore.selectedIndustry ? logoStore.selectedIndustry.name : ""),  set: (val) => {    if (logoStore.selectedIndustry) {      logoStore.selectedIndustry.name = val;    }  }});const industries = ref(industryData);const results = ref([]);const showDropdown = ref(false);const highlightedIndex = ref(-1);// Business details related fieldsconst businessNameInitials = computed({  get: () => logoStore.businessNameInitials,  set: (val) => { logoStore.businessNameInitials = val; }});const fullBusinessName = computed({  get: () => logoStore.fullBusinessName,  set: (val) => { logoStore.fullBusinessName = val; }});const businessCategory = computed({  get: () => logoStore.businessCategory,  set: (val) => { logoStore.businessCategory = val; }});const selectedLogoStyle = computed({  get: () => logoStore.selectedLogoStyle,  set: (val) => { logoStore.selectedLogoStyle = val; }});const sloganOrTagline = computed({  get: () => logoStore.sloganOrTagline,  set: (val) => { logoStore.sloganOrTagline = val; }});// Add methods to save state on changesconst changeSelectedLogoStyle = (newStyle) => {  selectedLogoStyle.value = newStyle;};// suffix statesconst suffixInputRef = ref(null);const suffixNames = ref(allSuffixes);const selectedSuffix = computed({  get: () => logoStore.selectedSuffix,  set: (val) => { logoStore.selectedSuffix = val; }});const showSuffixDropdown = ref(false);const highlightedSuffixIndex = ref(-1);const changeSelectedSuffix = (newSuffix) => {  selectedSuffix.value = newSuffix;};const selectSuffix = (suffix) => {  selectedSuffix.value = suffix.name  showSuffixDropdown.value = false;  if (suffixInputRef.value) {    suffixInputRef.value.blur();  }};const toggleSuffixDropdown = () => {  showSuffixDropdown.value = !showSuffixDropdown.value;};// symbols functions to change / select symbolsconst allSymbolsList = ref(allSymbols);const maxSymbols   = 3;// bind directly into Piniaconst selectedSymbolsOrStyles = computed({  get: () => logoStore.preferredSymbolsOrStyles,  set: (val) => { logoStore.preferredSymbolsOrStyles = val; }});const showSymbolsDropdown = ref(false);const symbolsWrapperRef = ref(null);// toggle dropdown open/closedfunction toggleSymbolsDropdown() {  showSymbolsDropdown.value = !showSymbolsDropdown.value;}// close when clicking outsidefunction closeSymbolDropdown(e) {  if (      symbolsWrapperRef.value &&      !symbolsWrapperRef.value.contains(e.target)  ) {    showSymbolsDropdown.value = false  }}// add/remove, enforcing max of 3function toggleSymbolOrStyle(symbol) {  const arr = selectedSymbolsOrStyles.value;  const idx = arr.findIndex(s => s.id === symbol.id);  if (idx >= 0) {    arr.splice(idx, 1);  } else if (arr.length < maxSymbols) {    arr.push(symbol);  }}function isSymbolActive(symbol) {  return selectedSymbolsOrStyles.value.some(s => s.id === symbol.id);}// color pickerconst selectedColorStyles = computed({  get: () => logoStore.selectedColorStyles,  set: (val) => { logoStore.selectedColorStyles = val; }});const showColorPickerDropdown = ref(false);const colorPickerWrapperRef = ref(null)// Logic for color pickerconst toggleColorPicker = () => {  showColorPickerDropdown.value = !showColorPickerDropdown.value}const closeColorPickerDropdown = (e) => {  if (      colorPickerWrapperRef.value &&      !colorPickerWrapperRef.value.contains(e.target)  ) {    showColorPickerDropdown.value = false  }}const toggleSelectedColor = (color) => {  const index = selectedColorStyles.value.indexOf(color);  if (index > -1) {    selectedColorStyles.value.splice(index, 1);  } else if (selectedColorStyles.value.length < 2) {    selectedColorStyles.value.push(color);  }};// Toggle industries dropdown visibility when the input is clickedconst toggleDropdown = () => {  showDropdown.value = !showDropdown.value;};// Close industries dropdown on outside clickconst closeDropdown = (event) => {  const clickedInside = industryInputRef.value?.contains(event.target);  if (!clickedInside) {    showDropdown.value = false;  }};// Fuse.js for industry searchconst fuse = new Fuse(industries.value, {  keys: ["name"],  threshold: 0.3,});// Search logicconst searchIndustries = () => {  const queryValue = industrySearchQuery.value.trim();  if (queryValue) {    results.value = fuse.search(queryValue).map((result) => result.item);  } else {    results.value = industries.value; // Show all industries when query is empty  }};// Select an industryconst selectIndustry = (industry) => {  selectedIndustry.value = industry;  industrySearchQuery.value = industry.name;  showDropdown.value = false;  if (industryInputRef.value) {    industryInputRef.value.blur();  }};const focusedInputField = ref(false); // Track the focused state of the input// Handle focus eventconst handleIndustryFocus = (event) => {  focusedInputField.value = true;  showDropdown.value = true;  results.value = industries.value; // Show all industries on focus  handleInputFieldWithArrowActiveState(event);};const handleIndustryBlur = (event) => {  focusedInputField.value = false;  handleInputFieldWithArrowBlurState(event);  setTimeout(() => {    showDropdown.value = false; // Delay to allow mousedown selection  }, 200);};const handleInputFieldWithArrowActiveState = (event) => {  const parent = event.target.closest('.field-item-input');  const arrowIcon = parent.querySelector('.arrow-icon');  if (arrowIcon) {    arrowIcon.classList.add('active');  }  else {    arrowIcon.classList.remove('active');  }};const handleInputFieldWithArrowBlurState = (event) => {  // Remove the active state from the arrow icon on blur  const parent = event.target.closest('.field-item-input');  const arrowIcon = parent?.querySelector('.arrow-icon');  if (arrowIcon) {    arrowIcon.classList.remove('active');  }}// Keyboard navigationconst handleKeyDown = (event) => {  if (!showDropdown.value || results.value.length === 0) return;  if (event.key === "ArrowDown") {    highlightedIndex.value = (highlightedIndex.value + 1) % results.value.length;  } else if (event.key === "ArrowUp") {    highlightedIndex.value = (highlightedIndex.value - 1 + results.value.length) % results.value.length;  } else if (event.key === "Enter" && highlightedIndex.value >= 0) {    selectIndustry(results.value[highlightedIndex.value]); // Select highlighted item  }};// Validationconst isValidSelection = computed(() =>    industries.value.some((industry) => industry.name.toLowerCase() === industrySearchQuery.value.toLowerCase()));const sidebarRef = ref(null);const mainColumnRef = ref(null);// DATA FOR SELECTING AND EDITING LOGOconst logoEditorSelected = computed(() => logoStore.logoEditorSelected);const selectedLogoID     = computed(() => logoStore.selectedLogoID);// Function to activate the logo editorconst activateLogoEditor = (logoID) => {  selectedLogoID.value = logoID; // Set the selected logo ID  logoEditorSelected.value = true; // Switch to the logo editor view};// Optional: Method to reset to default viewconst deactivateLogoEditor = () => {  selectedLogoID.value = ""; // Reset selected logo ID  logoEditorSelected.value = false; // Switch back to default view};// edit logo details mobile buttonconst ShowEditLogoDetailsMobile = ref(false);watch(    () => ({      selectedIndustry: logoStore.selectedIndustry,      selectedLogoStyle: logoStore.selectedLogoStyle,      selectedSuffix: logoStore.selectedSuffix,      selectedColorStyles: logoStore.selectedColorStyles,      sloganOrTagline: logoStore.sloganOrTagline,      preferredSymbolsOrStyles: logoStore.preferredSymbolsOrStyles,      fullBusinessName: logoStore.fullBusinessName,      businessNameInitials: logoStore.businessNameInitials,      businessCategory: logoStore.businessCategory    }),    (newVal, oldVal) => {      // Whenever any field changes, check if the current state differs      checkUpdateButton();    },    { deep: true });onMounted(() => {  window.scrollTo({top: 0, behavior: "auto"});  results.value = industries.value;  window.addEventListener("keydown", handleKeyDown);  window.addEventListener("mousedown", closeDropdown);  window.addEventListener('mousedown', closeColorPickerDropdown)  window.addEventListener('mousedown', closeSymbolDropdown);  // Clear any existing history (if any) and then capture the initial state.  logoStore.undoHistory = [];  logoStore.redoHistory = [];  const initialState = getCurrentLogoFieldValues();  logoStore.pushUndoHistory(initialState, maxSaveHistory);  originalLogoFieldValues.value = JSON.parse(JSON.stringify(initialState));  lastSavedLogoValues.value = JSON.parse(JSON.stringify(initialState));});// Clean up event listenersonUnmounted(() => {  window.removeEventListener("keydown", handleKeyDown);  window.removeEventListener("mousedown", closeDropdown);  window.removeEventListener("mousedown", closeColorPickerDropdown);  window.removeEventListener('mousedown', closeSymbolDropdown);});const showSaveLogoModal = ref(true);</script><template>  <div class="logo-builder w-full relative">    <section class="logo-builder-section w-full relative flex flex-col justify-start items-start font-primary mt-[77px] md:mt-[80px] lg:mt-[95px]" v-if="logoStore.logoBuilder">      <!-- Mobile Top Fields  -->      <div class="mobile-logo-fields w-full relative overflow-hidden lg:hidden border-y-2 p-[12px] border-neutral-200">        <button            class="edit-logo-details-btn block max-w-fit px-[15px] text-white bg-brand-300 font-bold py-[15px] uppercase text-[12px] tracking-[1px] mx-auto"            @click="ShowEditLogoDetailsMobile = !ShowEditLogoDetailsMobile">          {{ ShowEditLogoDetailsMobile ? 'Close Logo Editor' : 'Open Logo Editor' }}        </button>        <div            class="selector-fields-wrapper relative w-full overflow-hidden transition-all duration-500 ease-in-out"            :style="ShowEditLogoDetailsMobile ? 'max-height: 1000px; padding-top: 12px;' : 'max-height: 0; paddin