<script setup>// Declare Propsconst { addonClasses, altText, imageSource, lazyLoad } = defineProps({  addonClasses: {    type: String,    required: false,    default: '',  },  altText: {    type: String,    required: false,    default: '',  },  imageSource: {    type: String,    required: false,    default: '',  },  lazyLoad: {    type: Boolean,    required: false,    default: true,  }});</script><template><client-only>  <img v-if="lazyLoad"       :class="[addonClasses, { 'lazy lazyload':  lazyLoad }]"       :src="imageSource" :alt="altText" loading="lazy"  />  <img      v-else      :class="addonClasses"      :src="imageSource"      :alt="altText"  /></client-only></template><style scoped lang="scss"></style>