<script setup>  import { ref } from 'vue';  import { useRouter } from 'vue-router';  const router = useRouter();  import { useLogoStore } from "~/stores/logoStores";  const logoStore = useLogoStore();  /* company name validation */  const companyNameField = ref('');  const error = ref(null);  const validateCompanyName = (value) => {    return value && value.trim().length > 0;  };  /* handle submit of form */  const handleSubmit = (event) => {    event.preventDefault();    if (!validateCompanyName(companyNameField.value)) {      error.value = 'Please enter your company name';      return;    }    error.value = '';    logoStore.companyName = companyNameField.value;    router.push('/create-my-logo');  };</script><template>  <section class="home-hero relative block w-full bg-[#F5F5F5] jsScroll">    <div class="home-container site-container w-full relative">      <div class="hero-text w-full relative pt-[90px] pb-[60px] md:pt-[50px] md:pb-[55px] lg:pt-[85px] lg:pb-[80px] xl:pb-[86px] font-primary text-center">        <h1 class="block test-v2 relative text-black font-[900] uppercase tracking-[-2px] text-[40px] sm:text-[48px] md:text-[60px] lg:text-[80px] xl:text-[90px] leading-[1] pb-[40px] lg:max-w-[900px] xl:max-w-[1156px] mx-auto fadeInUp opacity-0">          Is your logo as professional as your work?        </h1>         <h2 class="block relative text-black font-medium text-[28px] md:text-[32px] lg:text-[38px] xl:text-[44px] lg:leading-[44px] xl:leading-[48px] leading-[33px] tracking-[-1px] lg:max-w-[1024px] xl:max-w-[1156px] mx-auto fadeInUp opacity-0">           Premium, pre-designed logos for your trade. Built by AI. Customize and download in minutes.         </h2>        <!-- start now form -->        <form class="start-now-form mt-[45px] fadeInUp opacity-0 hidden md:block" id="start-now-homepage-form" @submit="handleSubmit">          <div class="form-wrapper w-full h-full relative flex flex-col md:flex-row md:items-center justify-center md:max-w-[685px] md:mx-auto">            <div class="company-field w-full relative flex flex-col justify-center self-stretch">              <label for="company_name" class="hidden h-0 overflow-hidden pointer-events-none"></label>              <input                  v-model="companyNameField"                  name="company_name"                  type="text"                  class="px-[25px] text-center bg-white text-[16px] font-bold text-[#6B7280] h-[64px] md:h-full"                  placeholder="Enter your company name">                <p v-if="error" class="text-red-500 mt-1 text-[15px] absolute bottom-[-30px] w-full">{{ error }}</p>            </div>            <div class="bottom-button relative flex flex-col justify-center items-center w-full pt-[5px] md:max-w-fit md:pt-0">              <button type="submit" class="cta-btn has-arrow bg-black text-white flex flex-row flex-nowrap justify-center items-center relative w-full uppercase font-black text-[18px] leading-[1.125] tracking-[2px] py-[25px] px-[25px] sm:px-[33px] transition transition-fast hover:bg-brand-400 md:max-w-fit group ">                <span class="text relative flex-auto max-w-fit pr-[12px]">Start Now</span>                <span class="icon flex-0 max-w-fit w-auto relative group-hover:rotate-45 transition transition-fast">                <svg width="14" height="14" viewBox="0 0 14 14" fill="none" xmlns="http://www.w3.org/2000/svg">                    <path d="M0.75 13.25L13.25 0.75M13.25 0.75L3.875 0.75M13.25 0.75V10.125" stroke="white" stroke-width="1.5" stroke-linecap="round" stroke-linejoin="round"/>                </svg>            </span>              </button>            </div>          </div>        </form>      </div>    </div>  </section></template><style scoped lang="scss"></style>