<script setup>  import { useRouter } from 'vue-router';  const router = useRouter();  import { useLogoStore } from "~/stores/logoStores";  const logoStore = useLogoStore();  const handleSubmit = (event) => {    event.preventDefault();    const companyNameInput = document.querySelector("input[name='company_name']");    if (companyNameInput) {      // Update the global state value      logoStore.companyName = companyNameInput.value;      console.log("Updated global companyName:", logoStore.companyName);      // After updating the state, navigate to the /create-my-logo page      router.push('/create-my-logo');    }  };</script><template>  <section class="centered-text-cta relative block w-full font-primary pt-[90px] lg:pt-[100px] bg-brand-600 pb-[110px]">    <div class="site-container">      <div class="top-text relative block w-full text-center pb-[35px]">        <h3 class="font-black text-black text-[36px] md:text-[42px] lg:hidden leading-[1] text-center uppercase block relative max-w-[822px] mx-auto">          MAKE YOUR COMPETITION ENVY YOU EVEN MORE THAN THEY ALREADY DO        </h3>        <h3 class="font-black text-black text-[36px] hidden lg:block md:text-[42px] lg:text-[48px] leading-[1] text-center uppercase relative max-w-[822px] mx-auto">          Make your competition even more envious.        </h3>      </div>      <!-- start now form -->      <form class="start-now-form" id="start-now-homepage-form" @submit="handleSubmit">        <div class="form-wrapper w-full h-full relative flex flex-col md:flex-row md:items-center justify-center md:max-w-[685px] md:mx-auto">          <div class="company-field w-full relative flex flex-col justify-center self-stretch">            <label for="company_name" class="hidden h-0 overflow-hidden pointer-events-none"></label>            <input                name="company_name"                type="text"                class="px-[25px] text-center bg-white text-[16px] font-bold text-[#6B7280] h-[64px] md:h-full"                placeholder="Enter your company name"                required>          </div>          <div class="bottom-button relative flex flex-col justify-center items-center w-full pt-[5px] md:max-w-fit md:pt-0">            <button type="submit" class="cta-btn has-arrow bg-black text-white flex flex-row flex-nowrap justify-center items-center relative w-full uppercase font-black text-[18px] leading-[1.125] tracking-[2px] py-[25px]  px-[25px] sm:px-[33px] transition transition-fast hover:bg-black md:max-w-fit group">            <span class="text relative flex-auto max-w-fit pr-[12px]">               Start Now            </span>              <span class="icon flex-0 max-w-fit w-auto relative group-hover:rotate-45 transition transition-fast">                <svg width="14" height="14" viewBox="0 0 14 14" fill="none" xmlns="http://www.w3.org/2000/svg">                    <path d="M0.75 13.25L13.25 0.75M13.25 0.75L3.875 0.75M13.25 0.75V10.125" stroke="white" stroke-width="1.5" stroke-linecap="round" stroke-linejoin="round"/>                </svg>            </span>            </button>          </div>        </div>      </form>    </div>  </section></template><style scoped lang="scss"></style>