<template>  <div>    <div id="site-overlay" class="w-screen h-screen bg-black opacity-[0] pointer-events-none fixed inset-0 z-[1] transition transition-fast"></div>    <HeaderSection />        <main class="main">          <NuxtPage />        </main>    <LoginModal />    <SignupModal />    <FooterSection />  </div></template><script setup>import { reactive, provide, ref } from 'vue'import ImageElement from "~/components/ImageElement.vue";const globalCreationState = reactive({  creationStep: null,  companyName: null,  logoBuilder: false,})provide("globalCreationState", globalCreationState);const createMyLogoIsActive = ref(false);provide("createMyLogoIsActive", createMyLogoIsActive);/* login modal */const loginModalVisible = ref(false)const toggleLoginModal = () => {  loginModalVisible.value = !loginModalVisible.value}provide('loginModalVisible', loginModalVisible)provide('toggleLoginModal', toggleLoginModal)/* Signup Modal */const signupModalVisible = ref(false)const toggleSignupModal = () => {  signupModalVisible.value = !signupModalVisible.value}provide('signupModalVisible', signupModalVisible)provide('toggleSignupModal', toggleSignupModal)/* google auth stuff */const loginWithEmailAndPassword = ref('');const signUpWithGoogle = ref('');const signUpWithFacebook = ref('');const isUserLoggedOut =ref('');const isUserLoggedIn = ref(false);const userEmail = ref('');const userPassword = ref('');const loginUpdateStatus = ref('');const loginErrorMessage = ref('');const loggedInUserData = reactive({});function logInUser(user) {  isUserLoggedIn.value = true;  // Merge user data into your reactive object  Object.assign(loggedInUserData, user);  console.log('User logged in:', loggedInUserData);}function logOutUser() {  isUserLoggedIn.value = false;  for (const key in loggedInUserData) {    loggedInUserData[key] = null;  }  console.log('User logged out.');}provide("logInUser", logInUser);provide("loggedInUserData", loggedInUserData);provide("isUserLoggedIn", isUserLoggedIn);</script><style scoped></style>