<script setup>import { reactive, provide, ref, onMounted } from 'vue'import { getAuth, onAuthStateChanged, signOut } from 'firebase/auth'import HeaderSection from "../components/HeaderSection.vue";const globalCreationState = reactive({  creationStep: null,  companyName: null,  logoBuilder: false,})provide("globalCreationState", globalCreationState);const createMyLogoIsActive = ref(false);provide("createMyLogoIsActive", createMyLogoIsActive);/* login modal */const loginModalVisible = ref(false)const toggleLoginModal = () => {  loginModalVisible.value = !loginModalVisible.value}provide('loginModalVisible', loginModalVisible)provide('toggleLoginModal', toggleLoginModal)/* Signup Modal */const signupModalVisible = ref(false)const toggleSignupModal = () => {  signupModalVisible.value = !signupModalVisible.value}provide('signupModalVisible', signupModalVisible)provide('toggleSignupModal', toggleSignupModal)/* google auth stuff */const loginWithEmailAndPassword = ref('');const signUpWithGoogle = ref('');const signUpWithFacebook = ref('');const isUserLoggedOut =ref('');const isUserLoggedIn = ref(false);const userEmail = ref('');const userPassword = ref('');const loginUpdateStatus = ref('');const loginErrorMessage = ref('');const loggedInUserData = reactive({});function logInUser(user) {  isUserLoggedIn.value = true;  // Merge user data into your reactive object  Object.assign(loggedInUserData, user);  console.log('User logged in:', loggedInUserData);}function logOutUser(user) {  const auth = getAuth();  signOut(auth)      .then(() => {        isUserLoggedIn.value = false;        isUserLoggedOut.value = true; // Trigger logout message        Object.keys(loggedInUserData).forEach((key) => {          loggedInUserData[key] = null;        });        console.log("User signed out successfully.");      })      .catch((error) => {        console.error("Error signing out:", error);        alert("An error occurred while signing out. Please try again.");      });}provide("logInUser", logInUser);provide("logOutUser", logOutUser);provide("loggedInUserData", loggedInUserData);provide("isUserLoggedIn", isUserLoggedIn);onMounted(() => {  // This code runs only on the client.  const auth = getAuth();  onAuthStateChanged(auth, (user) => {    if (user) {      logInUser({        name: user.displayName,        email: user.email,        photo: user.photoURL,        uid: user.uid,      });    } else {      logOutUser();    }  });});</script><template>  <div>    <div id="site-overlay" class="w-screen h-screen bg-black opacity-[0] pointer-events-none fixed inset-0 z-[1] transition transition-fast"></div>    <HeaderSection />        <main class="main">          <NuxtPage :key="$route.fullPath" />        </main>    <LoginModal />    <SignupModal />    <FooterSection />  </div></template><style scoped></style>