<template>  <div>    <div id="site-overlay" class="w-screen h-screen bg-black opacity-[0] pointer-events-none fixed inset-0 z-[1] transition transition-fast"></div>    <HeaderSection />        <main class="main">          <NuxtPage :key="$route.fullPath" />          <transition>            <button id="back-to-top" class="back-to-top font-primary font-bold text-black text-[13px] text-center border border-neutral-200 border-r-0 bg-white flex flex-col justify-center items-center py-[8px] px-[12px] fixed right-[8px] bottom-[10vh] lg:bottom-[30vh] xl:bottom-[35vh] z-[99] leading-[1.2 uppercase tracking-[2px] transition transition-fast group hover:bg-brand-300 hover:border-brand-300" role="button" aria-label="Back To Top Of Page">              <span class="icon pb-[3px]">                  <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="text" class="size-6 stroke-brand-300 group-hover:stroke-white transition transition-fast">                    <path stroke-linecap="round" stroke-linejoin="round" d="M4.5 10.5 12 3m0 0 7.5 7.5M12 3v18" />                  </svg>              </span>              <span class="max-w-[120px] group-hover:text-white transition transition-fast">Top</span>            </button>          </transition>        </main>    <LoginModal />    <SignupModal />    <FooterSection />  </div></template><script setup>import { reactive, provide, ref } from 'vue'import ImageElement from "~/components/ImageElement.vue";const globalCreationState = reactive({  creationStep: null,  companyName: null,  logoBuilder: false,})provide("globalCreationState", globalCreationState);const createMyLogoIsActive = ref(false);provide("createMyLogoIsActive", createMyLogoIsActive);/* login modal */const loginModalVisible = ref(false)const toggleLoginModal = () => {  loginModalVisible.value = !loginModalVisible.value}provide('loginModalVisible', loginModalVisible)provide('toggleLoginModal', toggleLoginModal)/* Signup Modal */const signupModalVisible = ref(false)const toggleSignupModal = () => {  signupModalVisible.value = !signupModalVisible.value}provide('signupModalVisible', signupModalVisible)provide('toggleSignupModal', toggleSignupModal)/* google auth stuff */const loginWithEmailAndPassword = ref('');const signUpWithGoogle = ref('');const signUpWithFacebook = ref('');const isUserLoggedOut =ref('');const isUserLoggedIn = ref(false);const userEmail = ref('');const userPassword = ref('');const loginUpdateStatus = ref('');const loginErrorMessage = ref('');const loggedInUserData = reactive({});function logInUser(user) {  isUserLoggedIn.value = true;  // Merge user data into your reactive object  Object.assign(loggedInUserData, user);  console.log('User logged in:', loggedInUserData);}function logOutUser() {  isUserLoggedIn.value = false;  for (const key in loggedInUserData) {    loggedInUserData[key] = null;  }  console.log('User logged out.');}provide("logInUser", logInUser);provide("loggedInUserData", loggedInUserData);provide("isUserLoggedIn", isUserLoggedIn);</script><style scoped></style>