<script setup>import { onBeforeMount } from 'vue'import { useLogoStore } from '~/stores/logoStores.js'import LogoCreation from "~/components/CreationFlow/LogoCreation.vue";const logoStore = useLogoStore();onBeforeMount(() => {  const saved = sessionStorage.getItem('logoBuilderState')  if (saved) {    const state = JSON.parse(saved)    // patch only the fields you really need to restore    logoStore.$patch({      creationStep: state.creationStep,      logoBuilder: state.logoBuilder,      companyName: state.companyName,      selectedIndustry: state.selectedIndustry,      selectedLogoStyle: state.selectedLogoStyle,      fullBusinessName: state.fullBusinessName,      businessNameInitials: state.businessNameInitials,      selectedSuffix: state.selectedSuffix,      businessCategory: state.businessCategory,      sloganOrTagline: state.sloganOrTagline,      sloganOrTaglinePresent: state.sloganOrTaglinePresent,      preferredSymbolsOrStyles: state.preferredSymbolsOrStyles,      selectedColorStyles: state.selectedColorStyles,      logoGenerationLoading: state.logoGenerationLoading,      selectedLogoID: state.selectedLogoID,      logoEditorSelected: state.logoEditorSelected,      logoEditorLogoID: state.logoEditorLogoID,      undoHistory: state.undoHistory,      redoHistory: state.redoHistory,    })    sessionStorage.removeItem('logoBuilderState')  }})onMounted(() => {})useSeoMeta({  title: 'Cyborg Logo | Create My Logo',  ogTitle: 'Cyborg Logo | Create My Logo',  description: "Cyborg Logo LLC. The AI Logo Generator that's better than the rest.",  ogDescription: "Cyborg Logo LLC. The AI Logo Generator that's better than the rest.",  ogImage: '',  twitterCard: '',});</script><template>    <LogoCreation /></template><style scoped></style>